import page from '@/components/page';
export default {
  components: {
    page: page
  },
  data: function data() {
    return {
      title: '新增友情链接',
      dialogFormVisible: false,
      loading: true,
      links: [],
      form: {
        title: '',
        url: '',
        img: '',
        desc: '',
        end_time: ''
      },
      pageModel: {
        page: 1,
        all: 1,
        sumCount: 10
      }
    };
  },
  created: function created() {
    this.getLink();
  },
  methods: {
    getLink: function getLink() {
      var _this = this;

      this.$post('/api/v2/link/list', this.pageModel).then(function (res) {
        _this.links = res.data.data;
        _this.pageModel.sumCount = res.data.total;
        _this.loading = false;
      });
    },
    selectRoleList: function selectRoleList() {
      this.getLink();
    },
    deleteBtn: function deleteBtn(item) {
      var _this2 = this;

      this.$confirm('是否删除该链接?', '提示', {
        type: 'warning'
      }).then(function () {
        _this2.$post('/api/v2/link/delete', {
          id: item.id
        }).then(function (res) {
          _this2.$message.success(res.message);

          _this2.links.splice(_this2.links.indexOf(item), 1);
        });
      }).catch(function () {});
    },
    addBtn: function addBtn() {
      this.title = '新增友情链接';
      this.dialogFormVisible = true;
      this.form = {
        title: '',
        href: '',
        end_time: ''
      };
    },
    addSubmit: function addSubmit() {
      var _this3 = this;

      this.$post('/api/v2/link/add', this.form).then(function (res) {
        _this3.$message.success(res.message);

        _this3.dialogFormVisible = false;

        _this3.getLink();
      }).catch({});
    },
    editBtn: function editBtn(item) {
      this.title = '编辑友情链接';
      this.dialogFormVisible = true; // 复制对象不修改原对象

      this.form = Object.assign({}, item);
    },
    editSubmit: function editSubmit() {
      var _this4 = this;

      this.$post('/api/v2/link/edit', this.form).then(function (res) {
        _this4.$message.success(res.message);

        _this4.dialogFormVisible = false;

        _this4.getLink();
      }).catch({});
    }
  }
};
