import page from '@/components/page';
export default {
  components: {
    page: page
  },
  data: function data() {
    return {
      loading: true,
      message: [],
      selectMessage: [],
      pageModel: {
        page: 1,
        sumCount: 10
      }
    };
  },
  created: function created() {
    this.getMessage();
  },
  methods: {
    getMessage: function getMessage() {
      var _this = this;

      this.loading = true;
      this.$post('/api/v2/message/list', this.pageModel).then(function (res) {
        _this.message = res.data.data;
        _this.pageModel.sumCount = res.data.total;
        _this.loading = false;
      }).catch(function () {});
    },
    selectRoleList: function selectRoleList() {
      this.getMessage();
    },
    deleteBtn: function deleteBtn(item) {
      var _this2 = this;

      // console.log(item)
      this.$confirm('是否删除该留言?', '提示', {
        type: 'warning'
      }).then(function () {
        _this2.$post('/api/v2/message/deletes', {
          id: item.id
        }).then(function (res) {
          _this2.$message.success(res.message);

          _this2.message.splice(_this2.message.indexOf(item), 1);
        });
      }).catch(function () {});
    },
    // 打钩选择的数组id
    handleSelectionChange: function handleSelectionChange(rows) {
      var vArray = [];
      rows.forEach(function (item) {
        vArray.push(item.id);
      });
      this.selectMessage = vArray;
    },
    // 批量删除
    selectDelete: function selectDelete() {
      var _this3 = this;

      this.$confirm('是否删除选中的留言?', '提示', {
        type: 'warning'
      }).then(function () {
        _this3.$post('/api/v2/message/deletes', _this3.selectMessage).then(function (res) {
          _this3.$message.success(res.message);

          _this3.getMessage();
        });
      }).catch(function () {});
    }
  }
};
