import page from '@/components/page';
export default {
  components: {
    page: page
  },
  data: function data() {
    return {
      loading: true,
      comments: [],
      pageModel: {
        page: 1,
        sumCount: 10
      }
    };
  },
  created: function created() {
    this.getComment();
  },
  methods: {
    getComment: function getComment() {
      var _this = this;

      this.loading = true;
      this.$post('/api/v2/comment/list', this.pageModel).then(function (res) {
        console.log(res.data);
        _this.comments = res.data.data;
        _this.pageModel.sumCount = res.data.total;
        _this.loading = false;
      }).catch(function () {});
    },
    selectRoleList: function selectRoleList() {
      this.getComment();
    },
    deleteBtn: function deleteBtn(id) {
      var _this2 = this;

      this.$confirm('是否删除该评论?', '提示', {
        type: 'warning'
      }).then(function () {
        _this2.$post('/api/v2/comment/deletes', {
          id: id
        }).then(function (res) {
          _this2.$message.success(res.message);

          _this2.getComment();
        });
      });
    }
  }
};
