export default {
  data: function data() {
    return {
      loading: true,
      articles: [],
      pageModel: {
        page: 1,
        all: 1,
        sumCount: 10
      }
    };
  },
  created: function created() {
    this.getArticles();
  },
  methods: {
    getArticles: function getArticles() {
      var _this = this;

      this.loading = true; // 获取软删除的数据 all=1

      this.$post('/api/v2/article/list', this.pageModel).then(function (res) {
        _this.articles = res.data.data;
        _this.pageModel.sumCount = res.data.total; // 将已经下架的文章设置为true

        _this.articles.forEach(function (item) {
          if (item.deleted_at) {
            item.deleted_at = true;
          }
        });

        _this.loading = false;
      }).catch(function () {});
    },
    selectRoleList: function selectRoleList() {
      this.getArticles();
    },
    detail: function detail(id) {
      this.$router.push("/article/edit/".concat(id));
    },
    // 下架文章
    deleteBtn: function deleteBtn(id, value) {
      var _this2 = this;

      // 当value == true时操作下架，反之恢复文章
      if (value == true) {
        this.$post('/api/v2/article/delete', {
          id: id
        }).then(function (res) {
          console.log(res.data, 'delete');

          _this2.$message.success("\u6587\u7AE0".concat(id, "\u4E0B\u67B6\u64CD\u4F5C\u6210\u529F"));
        }).catch(function () {});
      } else {
        this.$post('/api/v2/article/restored', {
          id: id
        }).then(function (res) {
          console.log(res.data, 'restored');

          _this2.$message.success("\u6587\u7AE0".concat(id, "\u6062\u590D\u6210\u529F"));
        }).catch(function () {});
      }
    }
  }
};
