import page from '@/components/page'
export default {
  components: {
    page
  },
  data() {
    return {
      loading: true,
      comments: [],
      pageModel: {
        page: 1,
        sumCount: 10
      }
    }
  },
  created() {
    this.getComment()
  },
  methods: {
    getComment() {
      this.loading = true
      this.$post('/api/v2/comment/list', this.pageModel).then(res => {

        console.log(res.data)
        this.comments = res.data.data
        this.pageModel.sumCount = res.data.total
        this.loading = false
      }).catch(() => {
      })
    },
    selectRoleList() {
      this.getComment()
    },
    deleteBtn(id) {
      this.$confirm('是否删除该评论?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$post('/api/v2/comment/deletes', {id}).then(res => {
          this.$message.success(res.message)
          this.getComment()
        })
      })
    }
  }
}
