export default {
  data () {
    const validatePassCheck = (rule, value, callback) => {
      if (value === '') {
        callback(new Error('确认新密码不能为空'));
      } else if (value !== this.formCustom.new_password) {
        callback(new Error('两次输入的密码不一致!'));
      } else {
        callback();
      }
    };

    return {
      loading: false,
      alert: {
        type: 'error',
        msg: ''
      },
      formCustom: {
        old_password: '',
        new_password: '',
        rep_password: '',
      },
      ruleCustom: {
        old_password: [
          { required: true, message: '密码不能为空', trigger: 'change' }
        ],
        new_password: [
          { required: true, message: '新密码不能为空', trigger: 'change'}
        ],
        rep_password: [
          { required: true, validator: validatePassCheck, trigger: 'blur' }
        ]
      }
    }
  },
  methods: {
    // 修改密码按钮
    handleSubmit (name) {
      this.$refs[name].validate((valid) => {
        if (valid) {
          this.changePasswd()
        }
      })
    },
    // 修改密码接口
    changePasswd() {
      this.loading = true
      this.$post('/api/v2/user/resetpassword', this.formCustom).then(res => {
        this.alert = {
          type: 'success',
          msg: res.message
        }
        this.loading = false
        // this.formCustom = {
        //   old_password: '',
        //   new_password: '',
        //   rep_password: '',
        // }
        setTimeout(() => {
          this.$router.push('/person')
        }, 1500)
      }).catch(err => {
        this.loading = false
      })
    }
  }
}
