import { mapGetters, mapActions } from "vuex"
import Skills from '../components/skills'

export default {
  components: {
    Skills
  },
  data() {
    return {
      show: true,
      loading: false,
      checked: true,
      articles: [],
      classifys: [],
      pageModel: {
        page: Number(this.$route.query.page) || 1,
        sumCount: 10
      }
    }
  },
  computed: {
    ...mapGetters([
      'classify', 'tag'
    ])
  },
  created() {
    if (this.$route.query.tag) {
      this.ArticlesOrderByTag()
    } else if (this.$route.query.classify) {
      this.ArticlesOrderByClassify()
    } else {
      this.getArticles()
    }
  },
  methods: {
    ...mapActions([
      'Tag', 'Classify'
    ]),
    getArticles() {
      this.loading = true
      this.$post('/api/v2/article/list', this.pageModel).then(res => {
        this.pageModel.sumCount = res.data.total
        this.articles = res.data.data
        this.AddStaticUrl()
        this.Classify('all')
        this.loading = false
      })
    },
    selectRoleList() {
      if (this.$route.query.classify) {
        this.ArticlesOrderByClassify()
        this.$router.push({path:'/blog', query:{
          classify: this.$route.query.classify,
          page: this.pageModel.page
        }})
        console.log('请求分类分页')
      } else if (this.$route.query.tag) {
        this.ArticlesOrderByTag()
        this.$router.push({path:'/blog', query:{
          tag: this.$route.query.tag,
          page: this.pageModel.page
        }})
        console.log('请求标签分页')
      } else {
        this.getArticles()
        this.$router.push({path:'/blog', query:{
          page: this.pageModel.page
        }})
        console.log('请求正常分页')
      }
      window.scrollTo(0,0);
    },
    // 按标签获取
    ArticlesOrderByTag() {
      this.loading = true
      let param = {
        tag: this.$route.query.tag
      }
      this.$post('api/v2/tag/list', Object.assign(param, this.pageModel)).then(res => {
        this.pageModel.sumCount = res.data.total

        this.articles = []
        res.data.data.forEach(item => {
          this.articles.push(item.article)
        })
        this.AddStaticUrl()
        this.Tag(this.$route.query.tag)
        this.loading = false
      })
    },
    // 按分类获取
    ArticlesOrderByClassify() {
      this.loading = true
      let param = {
        classify: this.$route.query.classify
      }
      this.$post('/api/v2/article/list', Object.assign(param, this.pageModel)).then(res => {
        this.pageModel.sumCount = res.data.total
        this.articles = res.data.data
        this.AddStaticUrl()
        this.Classify(this.$route.query.classify)

        this.loading = false
      })
    },
    // 为静态图片添加域名
    AddStaticUrl() {
      this.articles.forEach(item => {
        if (item.img && item.img.indexOf("http")<0) {
          item.img = this.$staticUrl + item.img
        }
      });
    }
  }
}
