export default {
  data() {
    return {
      saveDis: false,
      loading: true,
      webinfo: {
        title: '',
        keyword: '',
        description: '',
        icp: '',
        weixin: '',
        zhifubao: '',
        description: '',
        weixin: '',
        zhifubao: '',
        personinfo: '',
      }
    }
  },
  created() {
    this.loading = true;
    this.$get('/api/v2/webinfo/read').then(res => {
      
      this.webinfo = res.data
      this.loading = false;
    }).catch(() => {

    })
  },
  methods: {
    submit() {
      this.loading = true;
      this.$post('/api/v2/webinfo/set', this.webinfo).then(res => {
        this.loading = false;
        this.$message.success(res.message)
      }).catch(() => {
      })
      
    }
  }
}
