import {mapActions, mapGetters} from "vuex"

export default {
  data() {
    return {
      title: '新增广告图',
      dialogFormVisible: false,
      loading: true,
      adlist: [],
      imageUrl: '',
      form: {
        title: '',
        url: '',
        type: ''
      },
      pageModel: {
        page: 1,
        sumCount: 10
      }
    }
  },
  computed: {
    ...mapGetters([
      'token'
    ]),
    headers() {
      return {
        'Authorization': this.token,
        'X-Requested-With': 'XMLHttpRequest' 
      }
    }
  },
  created() {
    this.getAd()
  },
  methods: {
    getAd() {
      this.$post('/api/v2/ad/list', this.pageModel).then(res => {
        console.log(res.data)
        this.adlist = res.data.data
        this.pageModel.sumCount = res.data.total
        this.loading = false
      })
    },
    selectRoleList() {
      this.getAd()
    },
    // 上传图片，获取图片地址
    handleAvatarSuccess(res, file) {
      // 如果已经有图片则先删除图片
      if (this.form.url) {
        this.handleRemove()
      }

      this.imageUrl = URL.createObjectURL(file.raw);
      this.$message.success('图片上传成功')
      this.form.url = res.data.url
    },
    // 删除图片
    handleRemove() {
      let param = {image: this.form.img}
      this.$post('/api/v2/image/delete', param).then(res => {
        this.$message.success(res.message)
      })
    },
    // 限制图片大小和格式
    beforeAvatarUpload(file) {
      const isJPG = file.type == 'image/jpeg' || 'image/png'
      const isLt2M = file.size / 1024 / 1024 < 2

      if (!isJPG) {
        this.$message.error('上传图片只能是 JPG和png 格式!')
      }
      if (!isLt2M) {
        this.$message.error('上传图片大小不能超过 2MB!')
      }
      return isJPG && isLt2M
    },
    deleteBtn(item) {
      this.$confirm('是否删除该广告图?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$post('/api/v2/ad/delete', {id: item.id}).then(res => {
          this.$message.success(res.message)
          this.adlist.splice(this.adlist.indexOf(item), 1)
        })
      }).catch(() => {     
      })
    },
    // 打开新增广告
    addBtn() {
      this.title = '新增友情链接'
      this.dialogFormVisible = true
      this.imageUrl = ''
      this.form = {
        title: '',
        href: '',
        end_time: ''
      }
    },
    // 提交新增
    addSubmit() {
      this.$post('/api/v2/ad/add', this.form).then(res => {
        this.$message.success(res.message)
        this.dialogFormVisible = false
        this.getAd()
      })
    },
    // 打开编辑
    editBtn(item) {
      this.title = '编辑广告图'
      this.dialogFormVisible = true
      // 复制对象不修改原对象
      this.form = Object.assign({}, item)
      this.imageUrl = this.$staticUrl + this.form.url
    },
    // 提交编辑
    editSubmit() {
      this.$post('/api/v2/ad/edit', this.form).then(res => {
        this.$message.success(res.message)
        this.dialogFormVisible = false
        this.getAd()
      })
    }
  }
}
