import headnav from './components/headnav'
import Footer from './components/footer'
import '@/style/index.styl'
import '@/style/hybrid.styl'
import {mapActions, mapGetters} from "vuex"

export default {
  name: 'app',
  components: {
    headnav,
    Footer,
  },
  computed: {
    ...mapGetters([
      'user',
      'token',
      'webInfo',
      'banners'
    ])
  }, 
  data: () => ({
    ops: {
      vuescroll: {},
      scrollPanel: {}
    }
  }),
  created() {
    // 有token,没有user时才请求用户信息
    if (this.token){
      this.UserInfo()
    }
    if (!this.webInfo){
      this.WebInfo()
    }
      this.Banners()
    if (!this.banners) {
    }
    // this.$Loading.config({
    //   color: '#fff',
    //   failedColor: '#000',
    //   height: 50
    // });

  },
  methods: {
    ...mapActions([
      'UserInfo',
      'WebInfo',
      'Banners'
    ]),
  }
}
