import {mapGetters} from "vuex"

export default {
  data() {
    return {
      classifys: []
    }
  },
  props: {pageModel: {}},
  created() {
    this.getClassify() 
  },
  computed: {
    ...mapGetters([
        'classify', 'tag'
    ]),
  },
  methods: {
    // 切换分类标签时，页码变为1
    allArticles() {
      this.$router.push('/blog')
      this.pageModel.page = 1
      this.$emit('getArticles');
    },
    OrderByClassify(item) {
      this.$router.push({path:'/blog', query:{classify: item}})
      this.pageModel.page = 1
      this.$emit('ArticlesOrderByClassify');
    },
    OrderByTag(item) {
      this.$router.push({path:'/blog', query:{tag: item}})
      this.pageModel.page = 1
      this.$emit('ArticlesOrderByTag');
    }, 
    // 获取所有分类
    getClassify() {
      this.$get('/api/v2/article/classify').then(res => {
        this.classifys = res.data
      }).catch(err =>{})
    }
  }
}
