import page from '@/components/page'
export default {
  components: {
    page
  },
  data() {
    return {
      loading: true,
      message: [],
      selectMessage: [],
      pageModel: {
        page: 1,
        sumCount: 10
      }
    }
  },
  created() {
    this.getMessage()
  },
  methods: {
    getMessage() {
      this.loading = true
      this.$post('/api/v2/message/list', this.pageModel).then(res => {
        this.message = res.data.data
        this.pageModel.sumCount = res.data.total

        this.loading = false
      }).catch(() => {
      })
    },
    selectRoleList() {
      this.getMessage()
    },
    deleteBtn(item) {
      // console.log(item)
      this.$confirm('是否删除该留言?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$post('/api/v2/message/deletes', {id: item.id}).then(res => {
            this.$message.success(res.message)
            this.message.splice(this.message.indexOf(item), 1)

        })
      }).catch(() => {     
      })
    },
    // 打钩选择的数组id
    handleSelectionChange(rows) {
      let vArray = []
      rows.forEach(item => {
          vArray.push(item.id);
      });
      this.selectMessage = vArray
    },
    // 批量删除
    selectDelete() {
      this.$confirm('是否删除选中的留言?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$post('/api/v2/message/deletes', this.selectMessage).then(res => {
          this.$message.success(res.message)
          this.getMessage()
        })
      }).catch(() => {     
      })
    }
  }
}
