export default {
  data() {
    return {
      loading: true,
      articles: [],
      pageModel: {
        page: 1,
        all: 1,
        sumCount: 10
      }
    }
  },
  created() {
    this.getArticles()
  },
  methods: {
    getArticles() {
      this.loading = true
      // 获取软删除的数据 all=1
      this.$post('/api/v2/article/list', this.pageModel).then(res => {

          this.articles = res.data.data
          this.pageModel.sumCount = res.data.total
          // 将已经下架的文章设置为true
          this.articles.forEach(item => {
            if (item.deleted_at) {
              item.deleted_at = true
            }
          })

        this.loading = false
      }).catch(() => {
      })
    },
    selectRoleList() {
      this.getArticles()
    },
    detail(id) {
      this.$router.push(`/article/edit/${id}`)
    },
    // 下架文章
    deleteBtn(id, value) {
      // 当value == true时操作下架，反之恢复文章
      if (value == true) {
        this.$post('/api/v2/article/delete', {id}).then(res => {
          console.log(res.data, 'delete')
          this.$message.success(`文章${id}下架操作成功`)
        }).catch(() => {
        })
      } else {
        this.$post('/api/v2/article/restored', {id}).then(res => {
          console.log(res.data, 'restored')
          this.$message.success(`文章${id}恢复成功`)
        }).catch(() => {
        })
      }
    }
  }
}
