import Axios from 'axios'
import {mapActions, mapGetters} from "vuex"

export default {
  data() {
    return {
      title: '写博客',
      blogBanner: '',
      loading: false,
      articleLoading: false,
      form: {
        title: '',
        desc: '',
        img: '',
        content: '',
        classify: '',
        tags: '',
        clicks: '',
        like :'',
        deleted_at: false,
        created_at: new Date()
      }
    }
  },
  computed: {
    ...mapGetters([
      'token'
    ]),
    headers() {
      return {
        'Authorization': this.token,
        'X-Requested-With': 'XMLHttpRequest' 
      }
    }
  },
  created() {
    if (this.$route.params.id) {
      this.getArticle()
    }
  },
  watch:{
    $route(to,from){
      this.blogBanner = '',
      this.form =  {
        title: '',
        desc: '',
        img: '',
        content: '',
        classify: '',
        tag: '',
        clicks: '',
        like :'',
        deleted_at: false,
        created_at: new Date()
      }
    },
  },
  methods: {
    addBtn() {
      this.loading = true
      this.$post('/api/v2/article/add', this.form).then(res => {
        this.$message.success(res.message)
        this.$router.push('/articlelist')
        this.loading = false
      }).catch(() => {
        this.loading = false
      })
    },
    getArticle() {
      this.articleLoading = true
      let params = {
        id: this.$route.params.id,
        all: 1
      }
      this.$post('/api/v2/article', params).then(res => {
        console.log(res.data)
        this.articleLoading = false
        this.form = res.data
        this.form.tags = res.data.tags.join(',')
        if (this.form.img) {
          this.blogBanner = this.$staticUrl+this.form.img
        }
      }).catch(() => {
      })
    },
    editBtn() {
      this.loading = true
      this.$post('/api/v2/article/edit', this.form).then(res => {
        this.$message.success(res.message)
        this.$router.push('/articlelist')
        this.loading = false
      }).catch(() => {
      })
    },
    // 上传图片，获取图片地址
    handleAvatarSuccess(res, file) {
      // 如果已经有图片则先删除图片
      if (this.form.img) {
        this.handleRemove()
      }

      this.blogBanner = URL.createObjectURL(file.raw);
      this.$message.success('图片上传成功')
      this.form.img = res.data.url
    },
    // 删除图片
    handleRemove() {
      let param = {image: this.form.img}
      this.$post('/api/v2/image/titleDelete', param).then(res => {
        this.$message.success(res.message)
      }).catch(() => {
      })
    },
    // 限制图片大小和格式
    beforeAvatarUpload(file) {
      const isJPG = file.type == 'image/jpeg' || 'image/png'
      const isLt3M = file.size / 1024 / 1024 < 3

      if (!isJPG) {
        this.$message.error('上传图片只能是 JPG和png 格式!')
      }
      if (!isLt3M) {
        this.$message.error('上传头像图片大小不能超过 3MB!')
      }
      return isJPG && isLt3M
    },

    // 绑定@imgAdd event 上传图片
    $imgAdd(pos, $file){
        let formdata = new FormData()
        formdata.append('image', $file)
        Axios({
            url: '/api/v2/image/upload',
            method: 'post',
            data: formdata,
            headers:{
              // 'Content-Type':'multipart/form-data',
              'Authorization': this.token,
              'X-Requested-With': 'XMLHttpRequest'
            }
        }).then((url) => {
          console.log(111, url, url.data.data.url, $file)
            // 第二步.将返回的url替换到文本原位置![...](0) -> ![...](url)
          this.$refs.md.$img2Url(pos, this.$staticUrl+url.data.data.url)
        }).catch(() => {
      })
    },
    // 因为拿不到图片名称，所以无法删除图片
    $imgDel(pos) {
        console.log(pos, '图片名')
        Axios({
            url: '/api/v2/image/delete',
            method: 'post',
            data: {
              image: pos
            },
            headers:{
              'Authorization': this.token,
              'X-Requested-With': 'XMLHttpRequest'
            }
        }).then((url) => {
          console.log(111, url, url.data.data.url, $file)
          this.$refs.md.$img2Url(pos, this.$staticUrl+url.data.data.url)
        }).catch(() => {
      })
    }
  }
}
