export default {

  data() {
    return {
      form: {
        old_password: '',
        new_password: '',
        re_password: ''
      }
    }
  },
  created() {

  },
  methods: {
    submit() {
      for (let item of Object.values(this.form)) {
        if (!item) {
          this.$message.error('密码不能为空')
          return false
        } 
      } 
      if (this.form.new_password != this.form.re_password) {
        this.$message.error('2次输入的密码不一致！')
      }
      this.$post('/api/v2/user/resetpassword', this.form).then(res => {
        this.$message.success(res.message)
        this.form = {
          old_password: '',
          new_password: '',
          re_password: ''
        }
      }).catch(() => {})
    }
  }
}
