import {mapGetters} from "vuex"
import marked from 'marked'
import '@/style/message.styl'

export default {
  data() {
    return {
      textarea: '支持markdown语法，尾部2个空格后回车才会换行，最长400个字',
      autofocus: false,
      loading: true,
      messageList: [],
      message:{
        content: '',
        ykname: ''
      },
      pageModel: {
        page: 1,
        sumCount: 10
      },
      page: 2,
      hasMore: true
    }
  },
  computed: {
    ...mapGetters([
        'user',
        'banners'
    ]),
    compiledMarkdown: function () {
      return marked(this.detail.content, { sanitize: false })
    }
  },
  created() {
    this.getMessage()
  },
  methods: {
    // 获取留言
    getMessage() {
      // this.loading = true
      this.$post('/api/v2/message/list', this.pageModel).then(res => {
        this.loading = false
        this.pageModel.sumCount = res.data.total
        this.messageList = res.data.data
        // 转markdown语法
        this.messageList.forEach(item => {
          item.content = marked(item.content, { sanitize: false })
          // 转换换行
          // item.content = item.content.replace(/\n/g, '<br>')
        })
      }).catch(() =>{})
    },    
    selectRoleList() {
      this.getMessage()
    },
    // 提交留言
    submitMessage() {
      this.$post('/api/v2/message/add', this.message).then(res => {
        this.getMessage()
        this.$Message.success(res.message)
        this.message = {
          content: '',
          name: ''
        }
      }).catch(() =>{})
    },
    // 删除自己的留言
    deleteComment(item) {
      this.$post('/api/v2/message/delete', {id: item.id}).then(res => {
        this.messageList.splice(this.messageList.indexOf(item), 1)
        this.$Message.success(res.message)
      }).catch(() =>{})
    },
    // 回复
    reply(id) {
      this.autofocus = true
      this.message.content = ''
      this.message.reply_id = id
      this.textarea = `回复 ${id}楼`
    }

  }
}
