import page from '@/components/page'
export default {
  components: {
    page
  },
  data() {
    return {
      title: '新增友情链接',
      dialogFormVisible: false,
      loading: true,
      links: [],
      form: {
        title: '',
        url: '',
        img: '',
        desc: '',
        end_time: ''
      },
      pageModel: {
        page: 1,
        all: 1,
        sumCount: 10
      }
    }
  },
  created() {
    this.getLink()
  },
  methods: {
    getLink() {
      this.$post('/api/v2/link/list', this.pageModel).then(res => {
        this.links = res.data.data
        this.pageModel.sumCount = res.data.total
        this.loading = false
      })
    },
    selectRoleList() {
      this.getLink()
    },
    deleteBtn(item) {
      this.$confirm('是否删除该链接?', '提示', {
        type: 'warning'
      }).then(() => {
        this.$post('/api/v2/link/delete', {id: item.id}).then(res => {
          this.$message.success(res.message)
          this.links.splice(this.links.indexOf(item), 1)
        })
      }).catch(() => {     
      })
    },
    addBtn() {
      this.title = '新增友情链接'
      this.dialogFormVisible = true
      this.form = {
        title: '',
        href: '',
        end_time: ''
      }
    },
    addSubmit() {
      this.$post('/api/v2/link/add', this.form).then(res => {
        this.$message.success(res.message)
        this.dialogFormVisible = false
        this.getLink()
      }).catch({})
    },
    editBtn(item) {
      this.title = '编辑友情链接'
      this.dialogFormVisible = true
      // 复制对象不修改原对象
      this.form = Object.assign({}, item)
    },
    editSubmit() {
      this.$post('/api/v2/link/edit', this.form).then(res => {
        this.$message.success(res.message)
        this.dialogFormVisible = false
        this.getLink()
      }).catch({})
    }
  }
}
