import {mapActions, mapGetters} from "vuex"
export default {
  data() {
    return {
      loading: true,
      links: [],
      pageModel: {
        page: 1,
        all: 1,
        sumCount: 100
      }
    }
  },
  computed: {
    ...mapGetters([
      'banners'
    ])
  }, 
  created() {
    this.getLink()
  },
  methods: {
    // 获取留言 all=1请求所有，不加的话请求的是有效期内的
    getLink() {
      this.$post('/api/v2/link/list', this.pageModel).then(res => {
        this.links = res.data.data
        this.loading = false
      }).catch(() => {})
    }
  }
}
